import "./Css/App.css";
import "./Css/index.css";
import "./Css/mandala.css";
import React, { useRef, useEffect } from "react";
import ReactDOM from "react-dom";
import Slider from "@material-ui/core/Slider";
import GridList from "@material-ui/core/GridList";
import GridListTile from "@material-ui/core/GridListTile";
import { gsap } from "gsap";
import makeConcentricRings from "./js/mandala.js";
import PathGallery from "./assets/PathGallery";

function App() {
  let svgContainer = useRef(null);
  let defs = useRef(null);
  let omegaDot = useRef(null);
  let rice = useRef(null);
  let petalDiamond = useRef(null);
  let petalHeart = useRef(null);

  //create default mandala
  useEffect(() => {
    let outerMandalaPetal = makeConcentricRings(
      [[omegaDot], [], [omegaDot], []],
      [
        { cuts: 21, radius: 1200 },
        { radius: 900 },
        { cuts: 14, radius: 600, scale: 0.7 },
        { radius: 300 },
      ],
      { id: "outerMandelaPetal" }
    );

    let testMandala = makeConcentricRings(
      [
        [outerMandalaPetal],
        [petalDiamond],
        [petalHeart],
        [omegaDot],
        [rice],
        [],
        [omegaDot],
      ],
      [
        { radius: 2100, cuts: 6, scale: 0.7, ringOffset: 0 },
        { radius: 2400, cuts: 6, ringOffset: 30 },
        { radius: 1400, cuts: 6 },
        { radius: 1100, cuts: 6, scale: 1.5 },
        { radius: 900, cuts: 30 },
        { radius: 750 },
        { radius: 500, cuts: 8 },
      ],
      { id: "testMandala" }
    );

    svgContainer.appendChild(testMandala);

    let tl = gsap.timeline({ paused: true });
    tl.to(
      ".ring:nth-child(even)",
      {
        duration: 15,
        transformOrigin: "center",
        ease: "none",
        rotation: 360,
        repeat: -1,
      },
      "ringSpin"
    );
    tl.to(
      ".ring:nth-child(odd)",
      {
        duration: 15,
        transformOrigin: "center",
        ease: "none",
        rotation: -360,
        repeat: -1,
      },
      "ringSpin"
    );
    testMandala.onclick = () => {
      tl.paused(!tl.paused());
    };
  });

  return (
    <div className="App">
      <header className="App-header"></header>
      <div id="canvas">
        <svg
          id="svgContainer"
          ref={(el) => {
            svgContainer = el;
          }}
          xmlns="http://www.w3.org/2000/svg"
          viewBox="-15360, -8640, 30720, 17280"
        >
          <PathGallery />
        </svg>
      </div>
      <div id="pathManipulationMenu">
        <label id="discrete-slider-cuts">Cuts</label>
        <Slider
          id="sliderCuts"
          defaultValue={0}
          // getAriaValueText="create function to get aria text"
          aria-labelledby="discrete-slider-cuts"
          valueLabelDisplay="auto"
          min={0}
          max={100}
        />
        <label id="discrete-slider-radius">Radius</label>
        <Slider
          id="sliderRadius"
          defaultValue={10}
          // getAriaValueText="create function to get aria text"
          aria-labelledby="discrete-slider-radius"
          valueLabelDisplay="auto"
          min={0}
          max={100}
        />
        <label id="discrete-slider-scale">Scale</label>
        <Slider
          id="sliderScale"
          defaultValue={1}
          // getAriaValueText="create function to get aria text"
          aria-labelledby="discrete-slider-scale"
          valueLabelDisplay="auto"
          step={0.1}
          marks
          min={0}
          max={2}
        />
        <label id="discrete-slider-offset">Offset</label>
        <Slider
          id="sliderOffset"
          defaultValue={0}
          // getAriaValueText="create function to get aria text"
          aria-labelledby="discrete-slider-offset"
          valueLabelDisplay="auto"
          steps={1}
          marks
          min={-180}
          max={180}
        />
      </div>
      {/* end of path sliders */}
      {/* <div id="pathGallery" className={classes.root}></div> */}
    </div>
  );
}

export default App;
