import React, { Component } from "react";
import gsap from "gsap";
import * as svgGeneric from "../js/svgGeneric";
import * as gsapExtension from "../js/gsapExtension";
const pi = Math.PI;
let defaultRadius = 50;

//creates a circle from an array of svg paths, returns <g class="ring">
class SvgRing extends React.Component {
  render() {
    // super();
    console.log(this.props.pathArr);
    this.state = {
      pathArrLength: this.props.pathArr.length,
      ring: svgGeneric.group({ class: this.props.ringClass + " ring" }),
    };
    if (this.state.pathArrLength < 1)
      this.state.ring.appendChild(
        svgGeneric.circle({
          r: this.props.radius ? this.props.radius : defaultRadius,
          class: "mandalaCircle",
        })
      );
    else {
      let avgWidth = 0;
      let avgHeight = 0;
      let pathDataArr = [];

      this.props.pathArr.forEach((e) => {
        console.log(e.clientHeight);
        let bBox = e.getBBox();
        //any elements passed in that are not in dom will be added to defs, as will any elements in the dom that have height == 0 and width == 0
        if (!bBox.width && !bBox.height) {
          svgGeneric.addToDefs(e);
          bBox = e.getBBox();
        }
        avgWidth += bBox.width;
        avgHeight += bBox.height;
        pathDataArr.push([e.getAttribute("id"), e.getAttribute("class")]);
      });

      avgWidth /= this.state.pathArrLength;
      avgHeight /= this.state.pathArrLength;

      this.state = {
        radius: this.props.radius ? this.props.radius : avgHeight,
        circumference: this.state.radius * 2 * pi,
        cuts:
          !this.props.cuts || this.props.cuts < 1
            ? Math.floor(
                this.state.circumference / avgWidth / this.state.pathArrLength
              )
            : this.props.cuts,
        totalCuts: this.state.cuts * this.state.pathArrLength,
      };

      for (let i = 1; i <= this.state.totalCuts; i++) {
        let pathData = pathDataArr[i % this.state.pathArrLength];
        let partialPath = svgGeneric.use({
          href: `#${pathData[0]}`,
          class: pathData[1],
        });
        //center
        gsapExtension.centerElement(partialPath);
        //move to circumference
        let angleRad = (2 * pi * i) / this.state.totalCuts;
        let scale = this.props.scale ? this.props.scale : 1;
        gsapExtension.moveToCircumferenceAtAngle(
          partialPath,
          angleRad,
          this.state.radius,
          scale
        );
        if (this.props.offset && this.props.offest != 0)
          gsap.set(this.state.ring, {
            transformOrigin: "center",
            rotation: this.props.offset,
          });
        this.state.ring.appendChild(partialPath);
      }
    }
  // }

  // render() {
    return this.state.ring;
  }
}
export default SvgRing;
